mapper.select:
SELECT COUNT(*)
FROM blog
WHERE source_id = #{sourceId}

SELECT `blog_id`, `user`.`uid`, `username`, `category`.`category_id`, `name` as category, `title`, `is_original` as original, `pub_time`, `summary` FROM ( SELECT `blog_id`, `uid`, `title`, `is_original`, `pub_time`, `summary` FROM `blog`
WHERE uid = #{uid}
ORDER BY `pub_time` DESC LIMIT #{pageSize} OFFSET #{pageOffset} ) as P LEFT JOIN `user` ON `user`.`uid` = P.`uid` LEFT JOIN `category` ON `category`.`category_id` = `user`.`category_id`

( SELECT `blog_id`, `title`, `pub_time` FROM `blog` WHERE `pub_time` > #{time}
AND `uid` = #{uid}
ORDER BY `pub_time` LIMIT 1 ) UNION ( SELECT `blog_id`, `title`, `pub_time` FROM `blog` WHERE `pub_time` < #{time}
AND `uid` = #{uid}
ORDER BY `pub_time` DESC LIMIT 1 )
